"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

const rule = require("../../../lib/rules/avoid-jquery211-library");
const RuleTester = require("eslint").RuleTester;

//------------------------------------------------------------------------------
// Helpers
//------------------------------------------------------------------------------

const ruleTester = new RuleTester();
const ruleError = { messageId: "message2" };

ruleTester.run("avoid-jquery211-library", rule, {
    valid: [
        "var myLib = 'jquery-2.1.1.min.js';"
    ],
    invalid: [
        {
            code: "var dataverseLib = '../_static/_common/scripts/jquery-2.1.1.min.js';",
            errors: [ruleError]
        }
    ]
});
