var priorityMap = {
  marginLeft: 2,
  marginRight: 2,
  marginTop: 2,
  marginBottom: 2,
  paddingLeft: 2,
  paddingRight: 2,
  paddingBottom: 2,
  paddingTop: 2
};

export default function getPropertyPriority(property, enforceLonghandPriority) {
  if (!enforceLonghandPriority) {
    return 1;
  }

  if (priorityMap.hasOwnProperty(property)) {
    return priorityMap[property];
  }

  return 1;
}